package ru.tasu.directleader;

import org.json.JSONObject;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.graphics.drawable.BitmapDrawable;
import android.graphics.drawable.Drawable;
import android.os.Parcel;
import android.os.Parcelable;
import android.util.Base64;
import android.util.Log;

public class Rabotnic implements Parcelable {
    private static final String TAG = "Rabotnic";
    
    // Описание полей
    private String _code;
    private String _codeRab;
    private long _id;
    private String _login; // Наверно поле не используется
    private String _name;
    private String _photo;
    private String _podr;
    private String _post_kind;
    private int _total_jobs;
    private int _overdue_jobs;
    private int _current_jobs;
    /*
    {
        Code: "Д000086",
        Id: 103538,
        Login: "Login",
        Name: "Иванов Иван Иванович",
        Photo: ",
        Podr: "03",
        PostKind: "Начальник информационного отдела"
    }
    // */
    public Rabotnic(JSONObject json) {
        updateData(json);
    }
    public Rabotnic(String code, String codeRab, long id, String login, String name, String photo, String podr, String post_kind) {
        this._code = code;
        this._codeRab = codeRab;
        this._id = id;
        this._login = login;
        this._name = name;
        this._photo = photo;
        this._podr = podr;
        this._post_kind = post_kind;
    }
    public Rabotnic(Parcel in) {
        this._code = in.readString();
        this._codeRab = in.readString();
        this._id = in.readLong();
        this._login = in.readString();
        this._name = in.readString();
        this._photo = in.readString();
        this._podr = in.readString();
        this._post_kind = in.readString();
    }
    public void updateData(JSONObject data) {
        this._code = data.optString("Code");
        this._codeRab = data.optString("CodeRab");
        this._id = data.optLong("Id");
        this._login = data.optString("Login");
        this._name = data.optString("Name");
        this._photo = data.optString("Photo");
        this._podr = data.optString("Podr");
        this._post_kind = data.optString("PostKind");
    }
    public String getCode() {
        return this._code;
    }
    public String getCodeRab() {
        return this._codeRab;
    }
    public long getId() {
        return this._id;
    }
    public String getLogin() {
        return this._login;
    }
    public String getName() {
        return this._name;
    }
    public String getPhoto() {
        return this._photo;
    }
    public Drawable getUserDrawable() {
        return new BitmapDrawable(decodeBase64(this._photo));
    }
    public String getPodr() {
        return this._podr;
    }
    public String getPostKind() {
        return this._post_kind;
    }
    
    public void setTotalJobs(int count) {
        this._total_jobs = count;
    }
    public void setOverdueJobs(int count) {
        this._overdue_jobs = count;
    }
    public void setCurrentJobs(int count) {
        this._current_jobs = count;
    }
    public int getTotalJobs() {
        return this._total_jobs;
    }
    public int getOverdueJobs() {
        return this._overdue_jobs;
    }
    public int getCurrentJobs() {
        return this._current_jobs;
    }
    
    public static final Parcelable.Creator<Rabotnic> CREATOR = new Parcelable.Creator<Rabotnic>() {

        public Rabotnic createFromParcel(Parcel in) {
            return new Rabotnic(in);
        }

        public Rabotnic[] newArray(int size) {
            return new Rabotnic[size];
        }
    };
    @Override
    public int describeContents() {
        return 0;
    }
    @Override
    public void writeToParcel(Parcel parcel, int flags) {
        parcel.writeString(this._code);
        parcel.writeString(this._codeRab);
        parcel.writeLong(this._id);
        parcel.writeString(this._login);
        parcel.writeString(this._name);
        parcel.writeString(this._photo);
        parcel.writeString(this._podr);
        parcel.writeString(this._post_kind);
        
    }
    public static Bitmap decodeBase64(String input) 
    {
        byte[] decodedByte = Base64.decode(input, 0);
        return BitmapFactory.decodeByteArray(decodedByte, 0, decodedByte.length); 
    }
}
